import React, { useState, useEffect } from 'react'
import Logout from '../Auth/Logout';
import Quiz from './Quiz';
import Loader from '../../components/Loader'
import axios from 'axios'

const Welcome = (props) => {

    const [userSession, setUserSession] = useState(null);
    const [userData, setUserData] = useState({});

    useEffect(() => {
        let uid = localStorage.getItem('uid')
        axios.get(`https://marvel-d9dde.firebaseio.com/users/${uid}.json`).then((response)=>{
            console.log(response.data)    
            let info = response.data
            let userInfo = Object.values(info)[0]
            setUserData(userInfo)
        }).catch((err)=>{
            console.log(err)
        })    
    }, [props.history])

    const Welcome = (
        <div className='quiz-bg'>
            <div className="container">
                <Logout redirect={props}/>
                <Quiz userData={userData}/>
            </div>
        </div>
    )

    return <> { userSession === null ? <Loader loadingMsg={"Authentification ..."} styling={{textAlign: 'center', color: '#FFFFFF'}} /> : Welcome } </>

}

export default Welcome
