import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Login = (props) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState(''); 
    const [btn, setBtn] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (password.length > 5 && email !== '')
            setBtn(true)
        else 
            setBtn(false)
    }, [password, email])

    const handleSubmit = e => {
        e.preventDefault();

        const authData = {
            email: email,
            password: password,
            returnSecureToken: true
        }
        axios.post('https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyB3CxFD3U-IzVFVFbxpslmMF_dMVQ4t6fk', authData)
        .then(response =>{
            localStorage.setItem('idToken',response.data.idToken);
            localStorage.setItem('uid',response.data.localId);
            props.history.push('/welcome');
        }).catch(error =>{
            setError(error);
        })
    }

    return (
        <div className="signUpLoginBox">
            <div className="slContainer">
                <div className="formBoxLeftLogin">
                </div>
                <div className="formBoxRight">
                    <div className="formContent">
                    <h2>Connexion</h2>
                        <form onSubmit={handleSubmit}>

                            {error !== ''  && <span>{error.message}</span>}

                            <div className="inputBox">
                                <input onChange={e => setEmail(e.target.value)} value={email} type="email" autoComplete="off" required />
                                <label htmlFor="email">Email</label>
                            </div>

                            <div className="inputBox">
                                <input onChange={e => setPassword(e.target.value)} value={password} type="password" autoComplete="off" required />
                                <label htmlFor="password">Mot de passe</label>
                            </div>

                            { btn ? <button >Connexion</button> : <button disabled>Connexion</button>}

                        </form>
                        <div className='linkContainer'>
                            <Link className="simpleLink" to="/signup">Nouveau sur Marvel Quiz ? Inscrivez-vous maintenant.</Link>
                            <br />
                            <Link className="simpleLink" to="/forgetpassword">Mot de passe oublié? Récupérez-le ici.</Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login;
