import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Signup = (props) => {

    const data = {
        pseudo: '',
        email: '',
        password: '',
        confirmPassword: ''
    }

    const [loginData, setLoginData] = useState(data);
    const [error, setError] = useState('');

    const handleChange = e => {
        setLoginData({...loginData, [e.target.id] : e.target.value})
    }

    const handleSubmit = e => {
        e.preventDefault();
        const authData = {
            email: email,
            password: password,
            returnSecureToken: true
        }

        const userData = {
            pseudo : pseudo,
            email : email
        }

        axios.post('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB3CxFD3U-IzVFVFbxpslmMF_dMVQ4t6fk',authData)
        .then((response) => {
            setError('')
            setLoginData({...data})
            axios.post(`https://marvel-d9dde.firebaseio.com/users/${response.data.localId}.json`,userData).then(sendData=>{
                console.log(sendData)
            }).catch(err=>{
                console.log(err)
            })
            localStorage.setItem('idToken',response.data.idToken);
            localStorage.setItem('uid',response.data.localId);
            props.history.push('/welcome');
        })
        .catch(error => {
            setError(error)
            setLoginData({...data})
        })
    }

    const { pseudo, email, password, confirmPassword } = loginData;

    const btn = pseudo === '' || email === '' || password === '' || password !== confirmPassword
    ? <button disabled>Inscription</button> : <button>Inscription</button>

    return (
        <div className="signUpLoginBox">
            <div className="slContainer">
                <div className="formBoxLeftSignup">
                </div>
                <div className="formBoxRight">
                    <div className="formContent">
                        <form onSubmit={handleSubmit}>

                            {error !== '' && <span>{error.message}</span>}

                            <h2>Inscription</h2>
                            <div className="inputBox">
                                <input onChange={handleChange} value={pseudo} type="text" id="pseudo" autoComplete="off" required />
                                <label htmlFor="pseudo">Pseudo</label>
                            </div>

                            <div className="inputBox">
                                <input onChange={handleChange} value={email} type="email" id="email" autoComplete="off" required />
                                <label htmlFor="email">Email</label>
                            </div>

                            <div className="inputBox">
                                <input onChange={handleChange} value={password} type="password" id="password" autoComplete="off" required />
                                <label htmlFor="password">Mot de passe</label>
                            </div>

                            <div className="inputBox">
                                <input onChange={handleChange} value={confirmPassword} type="password" id="confirmPassword" autoComplete="off" required />
                                <label htmlFor="confirmPassword">Confirmer le mot de passe</label>
                            </div>

                            {btn}

                        </form>
                        <div className='linkContainer'>
                            <Link className="simpleLink" to="/login">Deja inscrit ? Connectez-vous.</Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Signup;
