import app from 'firebase/app';
import 'firebase/auth';

const config = {
    apiKey: "AIzaSyB3CxFD3U-IzVFVFbxpslmMF_dMVQ4t6fk",
    authDomain: "marvel-d9dde.firebaseapp.com",
    databaseURL: "https://marvel-d9dde.firebaseio.com",
    projectId: "marvel-d9dde",
    storageBucket: "marvel-d9dde.appspot.com",
    messagingSenderId: "486625227834",
    appId: "1:486625227834:web:f8ffa1eda51a6563653103",
    measurementId: "G-GB5907SJYV"
};

class Firebase {
    constructor() {
        app.initializeApp(config);
        this.auth = app.auth();
        //this.auth = permet d'acceder au differente methode que permet l'API firebase.
    }

    //inscription
    signupUser = (email, password) => 
    this.auth.createUserWithEmailAndPassword(email, password)

    //connexion
    loginUser = (email, password) => 
    this.auth.signInWithEmailAndPassword(email, password)

    //logout
    signoutUser = () => this.auth.signOut()

    //resetpassword
    passwordReset = email => this.auth.sendPasswordResetEmail(email); 
}

export default Firebase;
